q = [-pi/2;0;0;pi/2;0;0;0];
dt = 0.01;
for i = 1:4
    for t = 0:dt:1
        
        a1 = M(0,1)\bx(i,:)'; % 6 by 6 with a 6 by 1
        a2 = M(0,1)\by(i,:)';
        a3 = M(0,1)\bz(i,:)';
        
        xdot = [0    1   2*t    3*t^2  4*t^3  5*t^4]* a1; % 1 by 6 with a 6 by 1
        ydot = [0    1   2*t    3*t^2  4*t^3  5*t^4]* a2;
        zdot = [0    1   2*t    3*t^2  4*t^3  5*t^4]* a3;
        
        qdot = pinv(J(q(1),q(2),q(3),q(4),q(5),q(6),q(7)))*[xdot; ydot; zdot; 0;0; 0];
        q = q + qdot*dt;
        
        T1 = A_1(q(1));
        T2 = A_1(q(1))*A_2(q(2));
        T3 = A_1(q(1))*A_2(q(2))*A_3(q(3));
        T4 = A_1(q(1))*A_2(q(2))*A_3(q(3))*A_4(q(4));
